---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'div'> {
  variant?: 'default' | 'destructive' | 'success' | 'warning' | 'info';
}

const {
  variant = 'default',
  class: className,
  ...props
} = Astro.props;

const baseStyles = 'relative w-full rounded-lg p-4 [&>svg~*]:pl-7 [&>svg+div]:translate-y-[-3px] [&>svg]:absolute [&>svg]:left-4 [&>svg]:top-4';

const variantStyles = {
  default: 'bg-muted/80 text-foreground [&>svg]:text-foreground',
  destructive: 'bg-destructive/5 text-foreground [&>svg]:text-destructive',
  success: 'bg-success/5 text-foreground [&>svg]:text-success',
  warning: 'bg-warning/5 text-foreground [&>svg]:text-warning',
  info: 'bg-info/5 text-foreground [&>svg]:text-info',
};

const classes = cn(baseStyles, variantStyles[variant], className);
---

<div role="alert" class={classes} {...props}>
  <slot />
</div>

