---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'span'> {
  size?: 'sm' | 'default' | 'lg';
}

const {
  size = 'default',
  class: className,
  ...props
} = Astro.props;

const sizeStyles = {
  sm: 'size-8',
  default: 'size-10',
  lg: 'size-12',
};

const classes = cn(
  'relative flex shrink-0 overflow-hidden rounded-full',
  sizeStyles[size],
  className
);
---

<span class={classes} {...props}>
  <slot />
</span>

