---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  variant?:
    | "default"
    | "secondary"
    | "destructive"
    | "outline"
    | "success"
    | "warning"
    | "info";
  href?: string;
}

const { variant = "default", href, class: className, ...props } = Astro.props;

const baseStyles = cn(
  "inline-flex items-center gap-1 rounded-sm px-2.5 py-0.5 text-xs font-medium transition-colors",
  "focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  // Icon styling
  "[&_svg]:pointer-events-none [&_svg]:size-3 [&_svg]:shrink-0",
  // Icon positioning
  "[&>[data-icon=inline-start]]:order-first [&>[data-icon=inline-end]]:order-last",
);

const variantStyles = {
  default: "bg-primary text-primary-foreground hover:bg-primary/80",
  secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
  destructive:
    "bg-destructive/10 text-destructive hover:bg-destructive/20 dark:bg-destructive/20 dark:hover:bg-destructive/30",
  outline:
    "border border-input bg-transparent text-foreground hover:bg-accent hover:text-accent-foreground",
  success:
    "bg-success/10 text-success hover:bg-success/20 dark:bg-success/20 dark:hover:bg-success/30",
  warning:
    "bg-warning/10 text-warning hover:bg-warning/20 dark:bg-warning/20 dark:hover:bg-warning/30",
  info: "bg-info/10 text-info hover:bg-info/20 dark:bg-info/20 dark:hover:bg-info/30",
};

const classes = cn(baseStyles, variantStyles[variant], className);
---

{
  href ? (
    <a href={href} class={classes} {...props}>
      <slot />
    </a>
  ) : (
    <div class={classes} {...props}>
      <slot />
    </div>
  )
}
