---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  orientation?: "horizontal" | "vertical";
}

const {
  orientation = "horizontal",
  class: className,
  ...props
} = Astro.props;

const classes = cn(
  "inline-flex",
  orientation === "vertical" ? "flex-col" : "flex-row",
  className
);
---

<div
  role="group"
  class={classes}
  data-orientation={orientation}
  {...props}
>
  <slot />
</div>

<style>
  /* Horizontal orientation - connected buttons */
  [role="group"][data-orientation="horizontal"] > :global(*:not(:first-child):not(:last-child)) {
    border-radius: 0;
  }
  [role="group"][data-orientation="horizontal"] > :global(*:first-child:not(:last-child)) {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
  }
  [role="group"][data-orientation="horizontal"] > :global(*:last-child:not(:first-child)) {
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
  }

  /* Remove double borders for outline buttons */
  [role="group"][data-orientation="horizontal"] > :global(*:not(:first-child)) {
    margin-left: -1px;
  }

  /* Vertical orientation - connected buttons */
  [role="group"][data-orientation="vertical"] > :global(*:not(:first-child):not(:last-child)) {
    border-radius: 0;
  }
  [role="group"][data-orientation="vertical"] > :global(*:first-child:not(:last-child)) {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
  }
  [role="group"][data-orientation="vertical"] > :global(*:last-child:not(:first-child)) {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
  }

  /* Remove double borders for outline buttons */
  [role="group"][data-orientation="vertical"] > :global(*:not(:first-child)) {
    margin-top: -1px;
  }
</style>

---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  orientation?: "horizontal" | "vertical";
}

const {
  orientation = "vertical",
  class: className,
  ...props
} = Astro.props;

const classes = cn(
  "bg-border self-stretch",
  orientation === "horizontal" ? "h-px w-full" : "w-px",
  className
);
---

<div
  role="separator"
  aria-orientation={orientation}
  class={classes}
  {...props}
/>

---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"span"> {
  size?: "xs" | "sm" | "default" | "lg" | "xl";
}

const { size = "default", class: className, ...props } = Astro.props;

const sizeStyles = {
  xs: "h-7 px-2.5 text-xs",
  sm: "h-8 px-3 text-xs",
  default: "h-9 px-3 text-sm",
  lg: "h-10 px-4 text-base",
  xl: "h-11 px-5 text-lg",
};

const classes = cn(
  "inline-flex items-center justify-center font-medium text-muted-foreground bg-backgorund border border-input rounded-md",
  sizeStyles[size],
  className,
);
---

<span class={classes} {...props}>
  <slot />
</span>
