---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  /**
   * Layout direction for the input group
   * - "inline": Horizontal layout (default, for InputGroupInput)
   * - "block": Vertical layout (for InputGroupTextarea)
   */
  layout?: "inline" | "block";
}

const { layout = "inline", class: className, ...props } = Astro.props;

const classes = cn(
  "relative flex min-w-0 rounded-md border border-input bg-background",
  layout === "inline" ? "flex-row items-stretch" : "flex-col",
  className,
);
---

<div class={classes} data-input-group data-layout={layout} {...props}>
  <slot />
</div>

<style is:global>
  /* Container focus ring when input is focused */
  [data-input-group]:has([data-slot="input-group-control"]:focus),
  [data-input-group]:has(
    [data-popover] [data-slot="input-group-control"]:focus
  ) {
    box-shadow: 0 0 0 3px oklch(var(--primary) / 0.1);
  }

  /* Remove individual borders from children in inline layout */
  [data-input-group][data-layout="inline"] > [data-slot="input-group-control"] {
    border: none;
    border-radius: 0;
    box-shadow: none;
  }
  [data-input-group][data-layout="inline"]
    > [data-popover]
    [data-slot="input-group-control"] {
    border: none;
    border-radius: 0;
    box-shadow: none;
  }

  [data-input-group][data-layout="inline"]
    > [data-slot="input-group-control"]:focus {
    box-shadow: none;
    outline: none;
  }
  [data-input-group][data-layout="inline"]
    > [data-popover]
    [data-slot="input-group-control"]:focus {
    box-shadow: none;
    outline: none;
  }

  /* First child gets left border radius */
  [data-input-group][data-layout="inline"] > *:first-child {
    border-top-left-radius: calc(0.375rem - 1px);
    border-bottom-left-radius: calc(0.375rem - 1px);
  }

  /* Last child gets right border radius */
  [data-input-group][data-layout="inline"] > *:last-child {
    border-top-right-radius: calc(0.375rem - 1px);
    border-bottom-right-radius: calc(0.375rem - 1px);
  }

  /* Addon positioning via order */
  [data-input-group][data-layout="inline"]
    > [data-input-group-addon][data-align="inline-start"] {
    order: -1;
  }

  [data-input-group][data-layout="inline"]
    > [data-input-group-addon][data-align="inline-end"] {
    order: 1;
  }

  /* Add separator between addon and input */
  [data-input-group][data-layout="inline"]
    > [data-input-group-addon][data-align="inline-start"] {
    border-right: 1px solid oklch(var(--border));
  }

  [data-input-group][data-layout="inline"]
    > [data-input-group-addon][data-align="inline-end"] {
    border-left: 1px solid oklch(var(--border));
  }

  /* Block layout children */
  [data-input-group][data-layout="block"] > [data-slot="input-group-control"] {
    border: none;
    border-radius: 0;
    box-shadow: none;
  }

  [data-input-group][data-layout="block"]
    > [data-slot="input-group-control"]:focus {
    box-shadow: none;
    outline: none;
  }

  /* First child in block layout */
  [data-input-group][data-layout="block"] > *:first-child {
    border-top-left-radius: calc(0.375rem - 1px);
    border-top-right-radius: calc(0.375rem - 1px);
  }

  /* Last child in block layout */
  [data-input-group][data-layout="block"] > *:last-child {
    border-bottom-left-radius: calc(0.375rem - 1px);
    border-bottom-right-radius: calc(0.375rem - 1px);
  }

  /* Addon positioning for block layout */
  [data-input-group][data-layout="block"]
    > [data-input-group-addon][data-align="block-start"] {
    order: -1;
    border-bottom: 1px solid oklch(var(--border));
  }

  [data-input-group][data-layout="block"]
    > [data-input-group-addon][data-align="block-end"] {
    order: 1;
    border-top: 1px solid oklch(var(--border));
  }
</style>

---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  /**
   * Position of the addon relative to the input
   * - "inline-start": Left side of input (default)
   * - "inline-end": Right side of input
   * - "block-start": Above the textarea
   * - "block-end": Below the textarea
   */
  align?: "inline-start" | "inline-end" | "block-start" | "block-end";
}

const {
  align = "inline-start",
  class: className,
  ...props
} = Astro.props;

const isInline = align === "inline-start" || align === "inline-end";
const isBlock = align === "block-start" || align === "block-end";

const classes = cn(
  "flex items-center gap-1 text-muted-foreground",
  isInline && "px-3",
  isBlock && "px-3 py-2",
  className
);
---

<div
  class={classes}
  data-input-group-addon
  data-align={align}
  {...props}
>
  <slot />
</div>

---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"button"> {
  /**
   * Button size
   */
  size?: "xs" | "sm" | "icon-xs" | "icon-sm";
  /**
   * Button variant
   */
  variant?: "default" | "destructive" | "outline" | "secondary" | "ghost" | "link";
  /**
   * Disabled state
   */
  disabled?: boolean;
  /**
   * Button type
   */
  type?: "button" | "submit" | "reset";
}

const {
  size = "xs",
  variant = "ghost",
  disabled = false,
  type = "button",
  class: className,
  ...props
} = Astro.props;

const sizeStyles = {
  xs: "h-6 px-2 text-xs",
  sm: "h-7 px-2.5 text-xs",
  "icon-xs": "size-6",
  "icon-sm": "size-7",
};

const variantStyles = {
  default: "bg-primary text-primary-foreground hover:bg-primary/90",
  destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
  outline: "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
  secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
  ghost: "hover:bg-accent hover:text-accent-foreground",
  link: "text-primary underline-offset-4 hover:underline",
};

const classes = cn(
  "inline-flex items-center justify-center gap-1 whitespace-nowrap rounded-md text-sm font-medium",
  "transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
  "disabled:pointer-events-none disabled:opacity-50",
  sizeStyles[size],
  variantStyles[variant],
  className
);
---

<button
  type={type}
  class={classes}
  disabled={disabled}
  {...props}
>
  <slot />
</button>

---
import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"input"> {
  type?: "text" | "email" | "password" | "search" | "tel" | "url" | "number";
  placeholder?: string;
  disabled?: boolean;
  required?: boolean;
  name?: string;
  id?: string;
  value?: string;
}

const {
  type = "text",
  placeholder,
  disabled = false,
  required = false,
  name,
  id,
  value,
  class: className,
  ...props
} = Astro.props;

const classes = cn(
  "flex-1 min-w-0 h-9 px-3 py-2 text-sm bg-transparent",
  "placeholder:text-muted-foreground",
  "focus:outline-none",
  "disabled:cursor-not-allowed disabled:opacity-50",
  className
);
---

<input
  type={type}
  class={classes}
  placeholder={placeholder}
  disabled={disabled}
  required={required}
  name={name}
  id={id}
  value={value}
  data-slot="input-group-control"
  {...props}
/>

