import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"textarea"> {
  placeholder?: string;
  disabled?: boolean;
  required?: boolean;
  name?: string;
  id?: string;
  rows?: number;
  value?: string;
}

const {
  placeholder,
  disabled = false,
  required = false,
  name,
  id,
  rows = 4,
  value,
  class: className,
  ...props
} = Astro.props;

const classes = cn(
  "flex-1 min-w-0 w-full px-3 py-2 text-sm bg-transparent resize-none",
  "placeholder:text-muted-foreground",
  "focus:outline-none",
  "disabled:cursor-not-allowed disabled:opacity-50",
  className
);
---

<textarea
  class={classes}
  placeholder={placeholder}
  disabled={disabled}
  required={required}
  name={name}
  id={id}
  rows={rows}
  data-slot="input-group-control"
  {...props}
>{value}</textarea>
