---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'input'> {
  type?: 'text' | 'email' | 'password' | 'search' | 'tel' | 'url' | 'number';
  placeholder?: string;
  disabled?: boolean;
  required?: boolean;
  name?: string;
  id?: string;
  value?: string;
}

const {
  type = 'text',
  placeholder,
  disabled = false,
  required = false,
  name,
  id,
  value,
  class: className,
  ...props
} = Astro.props;

const classes = cn(
  'block w-full h-9 px-4 py-2 text-sm leading-tight align-middle',
  'bg-background border border-border rounded-md',
  'placeholder:text-muted-foreground',
  'transition duration-300 ease-in-out',
  'focus:z-10 focus:border-primary focus:ring-[3px] focus:ring-primary/5 focus:outline-none',
  'file:border-0 file:bg-transparent file:text-sm file:font-medium file:text-foreground',
  'disabled:cursor-not-allowed disabled:opacity-50',
  className
);
---

<input
  type={type}
  class={classes}
  placeholder={placeholder}
  disabled={disabled}
  required={required}
  name={name}
  id={id}
  value={value}
  {...props}
/>
