import { cn } from "@/utils/cn";
import type { HTMLAttributes } from "astro/types";

export interface Props extends HTMLAttributes<"div"> {
  inset?: boolean;
  disabled?: boolean;
}

const { inset = false, disabled = false, class: className, ...props } = Astro.props;

const classes = cn(
  "relative flex cursor-pointer select-none items-center gap-2 rounded-sm px-2 py-1.5 text-sm outline-none",
  "focus:bg-accent focus:text-accent-foreground",
  "data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
  "[&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  inset && "pl-8",
  className
);
---

<div
  class={classes}
  data-menubar-item
  data-disabled={disabled ? "" : undefined}
  role="menuitem"
  tabindex={disabled ? -1 : 0}
  {...props}
>
  <slot />
</div>

<script>
  document.querySelectorAll("[data-menubar-item]:not([data-disabled])").forEach((item) => {
    item.addEventListener("click", () => {
      // Close the menubar after clicking an item
      const menubar = item.closest("[data-menubar]");
      if (menubar) {
        menubar.querySelectorAll("[data-menubar-content]").forEach((content) => {
          content.setAttribute("hidden", "");
        });
        menubar.querySelectorAll("[data-menubar-trigger]").forEach((trigger) => {
          trigger.setAttribute("aria-expanded", "false");
          trigger.setAttribute("data-state", "closed");
        });
      }
    });

    item.addEventListener("keydown", (e) => {
      if ((e as KeyboardEvent).key === "Enter" || (e as KeyboardEvent).key === " ") {
        e.preventDefault();
        (item as HTMLElement).click();
      }
    });
  });
</script>

