---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'div'> {}

const { class: className, ...props } = Astro.props;
---

<div class={cn('relative inline-block', className)} data-popover {...props}>
  <slot />
</div>

<script>
  import { ensureUiBootLoaded } from "@/utils/runtime/loader";

  ensureUiBootLoaded();
</script>

---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'div'> {
  align?: 'start' | 'center' | 'end';
  side?: 'top' | 'right' | 'bottom' | 'left';
  id?: string;
}

const {
  align = 'center',
  side = 'bottom',
  class: className,
  id,
  ...props
} = Astro.props;

const sideStyles = {
  top: 'bottom-full mb-2',
  right: 'left-full ml-2',
  bottom: 'top-full mt-2',
  left: 'right-full mr-2',
};

const alignStyles = {
  start: 'left-0',
  center: 'left-1/2 -translate-x-1/2',
  end: 'right-0',
};

const classes = cn(
  'absolute z-50 w-72 rounded-lg border bg-popover p-4 text-popover-foreground shadow-md outline-none',
  sideStyles[side],
  side === 'top' || side === 'bottom' ? alignStyles[align] : '',
  className
);
---

<div
  class={classes}
  data-popover-content
  role="dialog"
  aria-modal="false"
  id={id}
  hidden
  {...props}
>
  <slot />
</div>

---
import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'span'> {}

const { class: className, ...props } = Astro.props;
---

<span
  class={cn('inline-block cursor-pointer', className)}
  data-popover-trigger
  role="button"
  tabindex="0"
  aria-expanded="false"
  {...props}
>
  <slot />
</span>

<script>
  import { ensureUiBootLoaded } from "@/utils/runtime/loader";

  ensureUiBootLoaded();
</script>
